-- 培训内容段落表
CREATE TABLE IF NOT EXISTS training_segments (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  quiz_id UUID REFERENCES quizzes(id) ON DELETE CASCADE,
  chapter_num INT NOT NULL,
  title TEXT NOT NULL,
  timestamp TEXT,
  content TEXT NOT NULL,
  created_at TIMESTAMPTZ DEFAULT now()
);

-- 题目-段落关联表
CREATE TABLE IF NOT EXISTS question_sources (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  question_id UUID REFERENCES questions(id) ON DELETE CASCADE,
  segment_id UUID REFERENCES training_segments(id) ON DELETE CASCADE
);

-- RLS
ALTER TABLE training_segments ENABLE ROW LEVEL SECURITY;
ALTER TABLE question_sources ENABLE ROW LEVEL SECURITY;
CREATE POLICY "Anyone can read training_segments" ON training_segments FOR SELECT USING (true);
CREATE POLICY "Anyone can insert training_segments" ON training_segments FOR INSERT WITH CHECK (true);
CREATE POLICY "Anyone can read question_sources" ON question_sources FOR SELECT USING (true);
CREATE POLICY "Anyone can insert question_sources" ON question_sources FOR INSERT WITH CHECK (true);

-- 索引
CREATE INDEX IF NOT EXISTS idx_training_segments_quiz ON training_segments(quiz_id);
CREATE INDEX IF NOT EXISTS idx_question_sources_question ON question_sources(question_id);
CREATE INDEX IF NOT EXISTS idx_question_sources_segment ON question_sources(segment_id);

